import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { API, toast } from "@/App";
import axios from "axios";
import { Plus, Trash2, TrendingUp } from "lucide-react";
import { motion } from "framer-motion";

const CATEGORIES = [
  "Food & Dining",
  "Transportation",
  "Shopping",
  "Entertainment",
  "Bills & Utilities",
  "Healthcare",
  "Education",
  "Other"
];

export default function Budgets() {
  const [budgets, setBudgets] = useState([]);
  const [monthlyAnalytics, setMonthlyAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isOpen, setIsOpen] = useState(false);
  const [formData, setFormData] = useState({
    category: "",
    amount: "",
    period: "monthly",
    month: new Date().getMonth() + 1,
    year: new Date().getFullYear()
  });

  useEffect(() => {
    fetchBudgets();
    fetchMonthlyAnalytics();
  }, []);

  const fetchBudgets = async () => {
    try {
      const response = await axios.get(`${API}/budgets`);
      setBudgets(response.data);
    } catch (error) {
      toast.error("Failed to load budgets");
    } finally {
      setLoading(false);
    }
  };

  const fetchMonthlyAnalytics = async () => {
    try {
      const now = new Date();
      const response = await axios.get(`${API}/analytics/monthly`, {
        params: {
          year: now.getFullYear(),
          month: now.getMonth() + 1
        }
      });
      setMonthlyAnalytics(response.data);
    } catch (error) {
      console.error("Failed to load analytics", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API}/budgets`, {
        ...formData,
        amount: parseFloat(formData.amount)
      });
      toast.success("Budget created!");
      setIsOpen(false);
      resetForm();
      fetchBudgets();
      fetchMonthlyAnalytics();
    } catch (error) {
      toast.error(error.response?.data?.detail || "Failed to create budget");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this budget?")) return;
    try {
      await axios.delete(`${API}/budgets/${id}`);
      toast.success("Budget deleted");
      fetchBudgets();
      fetchMonthlyAnalytics();
    } catch (error) {
      toast.error("Failed to delete budget");
    }
  };

  const resetForm = () => {
    setFormData({
      category: "",
      amount: "",
      period: "monthly",
      month: new Date().getMonth() + 1,
      year: new Date().getFullYear()
    });
  };

  return (
    <div className="space-y-6" data-testid="budgets-page">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-4xl font-bold text-slate-800">Budgets</h1>
          <p className="text-slate-600 mt-1">Track and manage your spending limits</p>
        </div>
        <Dialog open={isOpen} onOpenChange={(open) => { setIsOpen(open); if (!open) resetForm(); }}>
          <DialogTrigger asChild>
            <Button className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 btn-animate" data-testid="add-budget-button">
              <Plus className="h-4 w-4 mr-2" />
              Add Budget
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md" data-testid="budget-dialog">
            <DialogHeader>
              <DialogTitle>Create Budget</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label>Category</Label>
                <Select value={formData.category} onValueChange={(value) => setFormData({ ...formData, category: value })}>
                  <SelectTrigger data-testid="budget-category-select">
                    <SelectValue placeholder="Select category" />
                  </SelectTrigger>
                  <SelectContent>
                    {CATEGORIES.map(cat => (
                      <SelectItem key={cat} value={cat}>{cat}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label>Amount</Label>
                <Input
                  type="number"
                  data-testid="budget-amount-input"
                  step="0.01"
                  placeholder="0.00"
                  value={formData.amount}
                  onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                  required
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label>Month</Label>
                  <Input
                    type="number"
                    data-testid="budget-month-input"
                    min="1"
                    max="12"
                    value={formData.month}
                    onChange={(e) => setFormData({ ...formData, month: parseInt(e.target.value) })}
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label>Year</Label>
                  <Input
                    type="number"
                    data-testid="budget-year-input"
                    min="2020"
                    value={formData.year}
                    onChange={(e) => setFormData({ ...formData, year: parseInt(e.target.value) })}
                    required
                  />
                </div>
              </div>
              <Button type="submit" className="w-full" data-testid="save-budget-button">
                Create Budget
              </Button>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {monthlyAnalytics && monthlyAnalytics.budget_status.length > 0 && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Card className="glass border-0 shadow-lg" data-testid="budget-status-card">
            <CardHeader>
              <CardTitle className="text-2xl font-bold text-slate-800">This Month's Budget Status</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {monthlyAnalytics.budget_status.map((budget, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="space-y-2"
                  data-testid={`budget-status-${index}`}
                >
                  <div className="flex items-center justify-between">
                    <span className="font-semibold text-slate-800">{budget.category}</span>
                    <span className="text-sm text-slate-600">
                      ${budget.spent.toFixed(2)} / ${budget.budget.toFixed(2)}
                    </span>
                  </div>
                  <Progress 
                    value={budget.percentage} 
                    className="h-3"
                    data-testid={`budget-progress-${index}`}
                  />
                  <div className="flex items-center justify-between text-sm">
                    <span className={budget.percentage > 90 ? "text-red-600 font-medium" : "text-slate-600"}>
                      {budget.percentage.toFixed(0)}% used
                    </span>
                    <span className={budget.remaining > 0 ? "text-green-600" : "text-red-600"}>
                      ${Math.abs(budget.remaining).toFixed(2)} {budget.remaining > 0 ? "remaining" : "over budget"}
                    </span>
                  </div>
                  {budget.percentage > 90 && (
                    <p className="text-xs text-red-600 font-medium" data-testid={`budget-warning-${index}`}>
                      ⚠️ Warning: You're approaching or exceeding your budget limit!
                    </p>
                  )}
                </motion.div>
              ))}
            </CardContent>
          </Card>
        </motion.div>
      )}

      {loading ? (
        <div className="text-center py-12">
          <p className="text-slate-600">Loading budgets...</p>
        </div>
      ) : budgets.length === 0 ? (
        <Card className="glass border-0 shadow-lg">
          <CardContent className="py-12 text-center">
            <TrendingUp className="h-16 w-16 mx-auto text-slate-400 mb-4" />
            <p className="text-lg text-slate-600">No budgets set</p>
            <p className="text-sm text-slate-500 mt-2">Create a budget to track your spending</p>
          </CardContent>
        </Card>
      ) : (
        <Card className="glass border-0 shadow-lg" data-testid="budgets-list">
          <CardHeader>
            <CardTitle className="text-xl font-bold text-slate-800">All Budgets</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {budgets.map((budget, index) => (
                <motion.div
                  key={budget.id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="flex items-center justify-between p-4 bg-slate-50 rounded-lg hover:bg-slate-100 smooth-transition"
                  data-testid={`budget-item-${index}`}
                >
                  <div>
                    <h3 className="font-semibold text-slate-800">{budget.category}</h3>
                    <p className="text-sm text-slate-600">
                      {budget.period === 'monthly' ? `Month ${budget.month}` : 'Yearly'} - {budget.year}
                    </p>
                  </div>
                  <div className="flex items-center gap-4">
                    <span className="text-lg font-bold text-slate-800">${budget.amount.toFixed(2)}</span>
                    <Button
                      size="sm"
                      variant="ghost"
                      data-testid={`delete-budget-${index}`}
                      onClick={() => handleDelete(budget.id)}
                      className="hover:bg-red-50 hover:text-red-600"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </motion.div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}
