import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { API, toast } from "@/App";
import axios from "axios";
import { Plus, Trash2, Target, TrendingUp } from "lucide-react";
import { motion } from "framer-motion";

export default function Goals() {
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isOpen, setIsOpen] = useState(false);
  const [updateOpen, setUpdateOpen] = useState(false);
  const [selectedGoal, setSelectedGoal] = useState(null);
  const [contributionAmount, setContributionAmount] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    target_amount: "",
    current_amount: "0",
    deadline: ""
  });

  useEffect(() => {
    fetchGoals();
  }, []);

  const fetchGoals = async () => {
    try {
      const response = await axios.get(`${API}/goals`);
      setGoals(response.data);
    } catch (error) {
      toast.error("Failed to load goals");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API}/goals`, {
        ...formData,
        target_amount: parseFloat(formData.target_amount),
        current_amount: parseFloat(formData.current_amount || 0),
        deadline: new Date(formData.deadline).toISOString()
      });
      toast.success("Goal created!");
      setIsOpen(false);
      resetForm();
      fetchGoals();
    } catch (error) {
      toast.error(error.response?.data?.detail || "Failed to create goal");
    }
  };

  const handleUpdateProgress = async () => {
    if (!selectedGoal || !contributionAmount) return;
    try {
      const newAmount = selectedGoal.current_amount + parseFloat(contributionAmount);
      await axios.put(`${API}/goals/${selectedGoal.id}`, {
        current_amount: newAmount
      });
      toast.success("Goal updated!");
      setUpdateOpen(false);
      setSelectedGoal(null);
      setContributionAmount("");
      fetchGoals();
    } catch (error) {
      toast.error("Failed to update goal");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this goal?")) return;
    try {
      await axios.delete(`${API}/goals/${id}`);
      toast.success("Goal deleted");
      fetchGoals();
    } catch (error) {
      toast.error("Failed to delete goal");
    }
  };

  const resetForm = () => {
    setFormData({
      name: "",
      target_amount: "",
      current_amount: "0",
      deadline: ""
    });
  };

  const openUpdateDialog = (goal) => {
    setSelectedGoal(goal);
    setUpdateOpen(true);
  };

  return (
    <div className="space-y-6" data-testid="goals-page">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-4xl font-bold text-slate-800">Savings Goals</h1>
          <p className="text-slate-600 mt-1">Set and track your financial goals</p>
        </div>
        <Dialog open={isOpen} onOpenChange={(open) => { setIsOpen(open); if (!open) resetForm(); }}>
          <DialogTrigger asChild>
            <Button className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 btn-animate" data-testid="add-goal-button">
              <Plus className="h-4 w-4 mr-2" />
              Add Goal
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md" data-testid="goal-dialog">
            <DialogHeader>
              <DialogTitle>Create Savings Goal</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label>Goal Name</Label>
                <Input
                  data-testid="goal-name-input"
                  placeholder="e.g., Emergency Fund"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  required
                />
              </div>
              <div className="space-y-2">
                <Label>Target Amount</Label>
                <Input
                  type="number"
                  data-testid="goal-target-input"
                  step="0.01"
                  placeholder="0.00"
                  value={formData.target_amount}
                  onChange={(e) => setFormData({ ...formData, target_amount: e.target.value })}
                  required
                />
              </div>
              <div className="space-y-2">
                <Label>Current Amount (Optional)</Label>
                <Input
                  type="number"
                  data-testid="goal-current-input"
                  step="0.01"
                  placeholder="0.00"
                  value={formData.current_amount}
                  onChange={(e) => setFormData({ ...formData, current_amount: e.target.value })}
                />
              </div>
              <div className="space-y-2">
                <Label>Deadline</Label>
                <Input
                  type="date"
                  data-testid="goal-deadline-input"
                  value={formData.deadline}
                  onChange={(e) => setFormData({ ...formData, deadline: e.target.value })}
                  required
                />
              </div>
              <Button type="submit" className="w-full" data-testid="save-goal-button">
                Create Goal
              </Button>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Update Progress Dialog */}
      <Dialog open={updateOpen} onOpenChange={setUpdateOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Update Progress</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div className="space-y-2">
              <Label>Add Contribution</Label>
              <Input
                type="number"
                data-testid="contribution-amount-input"
                step="0.01"
                placeholder="0.00"
                value={contributionAmount}
                onChange={(e) => setContributionAmount(e.target.value)}
              />
            </div>
            {selectedGoal && (
              <p className="text-sm text-slate-600">
                Current: ${selectedGoal.current_amount.toFixed(2)} â†’ New: ${(selectedGoal.current_amount + parseFloat(contributionAmount || 0)).toFixed(2)}
              </p>
            )}
            <Button onClick={handleUpdateProgress} className="w-full" data-testid="update-progress-button">
              Update Progress
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {loading ? (
        <div className="text-center py-12">
          <p className="text-slate-600">Loading goals...</p>
        </div>
      ) : goals.length === 0 ? (
        <Card className="glass border-0 shadow-lg">
          <CardContent className="py-12 text-center">
            <Target className="h-16 w-16 mx-auto text-slate-400 mb-4" />
            <p className="text-lg text-slate-600">No goals set</p>
            <p className="text-sm text-slate-500 mt-2">Create your first savings goal</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6" data-testid="goals-list">
          {goals.map((goal, index) => {
            const percentage = (goal.current_amount / goal.target_amount) * 100;
            const daysRemaining = Math.ceil(
              (new Date(goal.deadline).getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24)
            );

            return (
              <motion.div
                key={goal.id}
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <Card className="glass card-hover border-0 shadow-lg" data-testid={`goal-card-${index}`}>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <CardTitle className="text-xl font-bold text-slate-800">{goal.name}</CardTitle>
                      <Button
                        size="sm"
                        variant="ghost"
                        data-testid={`delete-goal-${index}`}
                        onClick={() => handleDelete(goal.id)}
                        className="hover:bg-red-50 hover:text-red-600"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-slate-600">Progress</span>
                      <span className="text-sm font-semibold text-slate-800">
                        ${goal.current_amount.toFixed(2)} / ${goal.target_amount.toFixed(2)}
                      </span>
                    </div>
                    <Progress value={percentage} className="h-3" data-testid={`goal-progress-${index}`} />
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-slate-600">{percentage.toFixed(0)}% complete</span>
                      <span className={daysRemaining > 0 ? "text-slate-600" : "text-red-600"}>
                        {daysRemaining > 0 ? `${daysRemaining} days left` : "Overdue"}
                      </span>
                    </div>
                    <Button
                      onClick={() => openUpdateDialog(goal)}
                      data-testid={`update-goal-${index}`}
                      className="w-full bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 btn-animate"
                    >
                      <TrendingUp className="h-4 w-4 mr-2" />
                      Add Contribution
                    </Button>
                  </CardContent>
                </Card>
              </motion.div>
            );
          })}
        </div>
      )}
    </div>
  );
}
